    await mb.addEntityAsync("data", "Form data", modelType);
    await mb.addEntityAsync("initialValues", "Initial values", modelType);
} else {
    mb.addObject("data", "Form data");
    mb.addObject("initialValues", "Initial values");    
};
mb.addObject("parentFormValues", "Parent form values. The values of the form rendering the dialog.");
mb.addStandard(["shesha:globalState", "shesha:moment"]);
return mb.build();

